export const SimpleDialog: Story<DialogComponentProps> = ({ body, onClose, title, ...args }) => {
  const { open, setOpen } = useDialogueHook();
  return (
    <>
      <Button color="primary" variant="contained" onClick={() => setOpen(!open)} size="medium">
        Click Here To Open Dialog
      </Button>
      <DialogComponent
        {...args}
        className="personalInfo-dialog-component"
        color="primary"
        open={open}
        /**
         * kept this or condition just to take dynamic args from stroybook UI
         */
        title={title || 'Simple Dialogue'}
        body={body || 'Dialog without close action or icon, please do a page refresh or click on canvas'}
      />
    </>
  );
};

